mapper.delete:
delete from fooditem where 1=1
and f_num in
                     
                         #{deleteKey}

mapper.select:
select * from fooditem where f_code like #{searchKeyword}
                        or f_title like #{searchKeyword}
                        or f_contents like #{searchKeyword}
                        or f_price like #{searchKeyword}
                        or f_count like #{searchKeyword}
                        or f_date like #{searchKeyword}
                        or f_edate like #{searchKeyword}

mapper.insert:
insert into fooditem (c_num, c_num2, f_title, f_contents, f_price, f_count, f_edate, f_code, f_volume, f_origin, f_cal, f_save)
values (#{fiBigCg}, #{fiMidCg}, #{fiTitle}, #{fiContents}, #{fiPrice},
        #{fiCounts}, #{fiExDate}, #{fiCode}, #{fiVolume}, #{fiOrigin}, #{fiCal}, #{fiSave});

    
        SELECT CAST(MAX(CAST(f_num AS UNSIGNED)) AS CHAR) FROM fooditem
    
insert into img (i_path, i_sys_name, i_original_name, f_num, m_id, i_filesize)
values (#{i_path}, #{i_sys_name}, #{i_original_name}, #{maxNum}, #{m_id}, #{i_filesize});
