mapper.delete:
delete from fooditem
where f_code = #{code}
and f_title = #{title}
and c_num = #{bigCgNum}

mapper.select:
select * from fooditem f
join category c
on f.c_num = c.c_num
where f.f_title = #{title}
and f.f_code = #{code}
and c.c_name = #{cgName}
and c.c_num = #{bigCgNum}

select * from img i
join fooditem f
on i.f_num =f.f_num
join category c
on c.c_num = f.c_num
where c.c_num = #{bigCgNum}
and f.f_title = #{title}
and f.f_code = #{code}
and c.c_name = #{cgName}
